import React, { useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAngleDown, faAngleUp } from "@fortawesome/free-solid-svg-icons";
import { Col, Row, Table, Image, Container } from '@themesberg/react-bootstrap';
import axios from "axios"; // Import axios
import Documentation from "../../components/Documentation";

export default () => {
  const [comments, setComments] = useState([]);

  // Fetch comments from the Flask API using axios
  useEffect(() => {
    const fetchComments = async () => {
      try {
        const response = await axios.get("http://localhost:5000/get_comments?video_id=WEBiebbeNCA");
        const data = response.data;
        if (data.comments) {
          const parsedComments = JSON.parse(data.comments);
          setComments(parsedComments);
        }
      } catch (error) {
        console.error("Error fetching comments:", error);
      }
    };

    fetchComments();
  }, []);

  return (
    <article>
      <Container className="px-0">
        <Row className="d-flex flex-wrap flex-md-nowrap align-items-center py-4">
          <Col className="d-block mb-4 mb-md-0">
            <h1 className="h2">Comments</h1>
            <p className="mb-0">
              Use this table to visualize comments fetched from the Flask API.
            </p>
          </Col>
        </Row>

        <Documentation
          title="Example"
          description={
            <>
              <p>The <code>&#x3C;Table&#x3E;</code> component is used to display comments fetched from the Flask API.</p>
            </>
          }
          scope={{ Image, Table, FontAwesomeIcon, faAngleDown, faAngleUp }}
          imports={`import { Image, Table } from '@themesberg/react-bootstrap';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAngleDown, faAngleUp } from "@fortawesome/free-solid-svg-icons";
import axios from "axios";`}
          example={`<Table>
  <thead className="thead-light">
    <tr>
      <th className="border-0">User</th>
      <th className="border-0">Comment</th>
      <th className="border-0">Date</th>
      <th className="border-0">Replies</th>
    </tr>
  </thead>
  <tbody>
    {comments.map((comment, index) => (
      <tr key={index}>
        <td className="border-0">
          <div className="d-flex align-items-center">
            <div><span className="h6">{comment.user_name}</span></div>
          </div>
        </td>
        <td className="border-0 fw-bold">{comment.comment}</td>
        <td className="border-0">{new Date(comment.date).toLocaleString()}</td>
        <td className="border-0">
          {comment.replies.length > 0 ? (
            <div>
              {comment.replies.map((reply, replyIndex) => (
                <div key={replyIndex} className="text-muted">{reply}</div>
              ))}
            </div>
          ) : (
            <span className="text-muted">No replies</span>
          )}
        </td>
      </tr>
    ))}
  </tbody>
</Table>`}
        />
      </Container>
    </article>
  );
};
